import 'bootstrap';

/**
 * We'll load the axios HTTP library which allows us to easily issue requests
 * to our Laravel back-end. This library automatically handles sending the
 * CSRF token as a header based on the value of the "XSRF" token cookie.
 */

import axios from 'axios';
window.axios = axios;

window.axios.defaults.headers.common['X-Requested-With'] = 'XMLHttpRequest';

// Add a global Axios interceptor to handle 419 (Page Expired) errors
axios.interceptors.response.use(
    response => response,
    error => {
        // Check if the error is a 419 status (Page Expired)
        if (error.response && error.response.status === 419) {
            // Display a message to the user (optional, you can remove this if you prefer silent refresh)
            console.warn('Session expired or CSRF token mismatch. Reloading page...');
            // Reload the page to get a fresh CSRF token and session
            window.location.reload();
        }
        return Promise.reject(error);
    }
);

